import React from 'react'
import { Container, Row, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';

const Town = (props) => {
    // const { title, clouds, temperature, wind } = this.props;
        return (
            // <div className="row justify-content-around">
            <Link to='/'>
                <Col xs={2} >props.title} 
                    <div className="row block align-items-center"> 
                        <div className="col">{props.temperature}</div>
                    </div>

                    <div className="row block align-items-center"> 
                        <div className="col">{props.wind}</div>
                    </div>

                    <div className="row block align-items-center"> 
                        <div className="col">{props.clouds}</div>
                    </div>
                </Col>
            </Link>    
            // </div>
        )
}

export default Town
