const Home = () => {


    const createTownslist = () => {
    var towns = ["Sudak", "Yalta", "Kerch", "Feodosiya"];
    var townslist = [];
    towns.map( (town) => {
        fetch(`https://api.weatherbit.io/v2.0/current?city=${town}&key=ced73555abfb464ebcb5d7e77f5be270`)
        .then(res => res.json())
        .then(json => pushToArr( townslist, json.data))
        return townslist;
