let towns = ["Sudak", "Yalta", "Kerch", "Feodosiya"];
let townslist = [];


async function getData() {
    const res = await fetch(`https://api.weatherbit.io/v2.0/current?city=Sudak&key=ced73555abfb464ebcb5d7e77f5be270`);
    const json = await res.json();
    return json
}

console.log(json);

getData()
    .then(townslist.push(json))


console.log(townslist)

const Home = () => {
    return(
            <div className="page">
                <Navbar /> 
                <Jumbotron title='Welcome' subtitle="Hello"/>

                <div className="home-container">
                    <Container>
                        <Townslist townslist={townslist} towns={towns} />
                    </Container>
                </div>  
                    <Footer />
            </div>
    )
}

export default Home
